import React from 'react';
import { Mail, Linkedin, Github, Twitter, Instagram } from 'lucide-react';
const Contact = () => {
    return (
        <section id="contact" className="py-20 px-8 sm:px-12 lg:px-16 relative">
        <div className="max-w-6xl mx-auto text-center">
          <h2 className="text-4xl md:text-5xl font-bold mb-8 bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent">
            Let's Work Together
          </h2>
          <p className="text-xl text-gray-300 mb-12 max-w-2xl mx-auto">
            Have a project in mind? I'd love to hear about it. Let's create something amazing together.
          </p>

          <div className="space-y-8">
            <a 
              href="mailto:john@example.com"
              className="inline-flex items-center space-x-3 px-8 py-4 bg-gradient-to-r from-purple-600 to-pink-600 rounded-full hover:from-purple-500 hover:to-pink-500 transition-all duration-300 hover:scale-105 hover:shadow-lg hover:shadow-purple-500/25"
            >
              <Mail size={20} />
              <span className="text-lg font-medium">Get In Touch</span>
            </a>

            <div className="flex justify-center space-x-8 pt-8">
              {[
                { icon: Github, href: "#", label: "GitHub" },
                { icon: Linkedin, href: "#", label: "LinkedIn" },
                { icon: Mail, href: "#", label: "Email" }
              ].map(({ icon: Icon, href, label }) => (
                <a
                  key={label}
                  href={href}
                  className="flex flex-col items-center space-y-2 p-4 rounded-xl bg-white/5 hover:bg-white/10 transition-all duration-300 hover:scale-105 group"
                >
                  <Icon size={24} className="group-hover:text-purple-400 transition-colors duration-300" />
                  <span className="text-sm text-gray-400 group-hover:text-gray-300">{label}</span>
                </a>
              ))}
            </div>
          </div>
        </div>
      </section>
    )
}

export default Contact;
