import React from 'react';
import { ChevronDown, Github, Linkedin, Mail } from 'lucide-react';
import { useTheme } from '../context/ThemeContext';

const Hero: React.FC = () => {
  const { theme } = useTheme();

  return (
    <section id="home" className="min-h-screen flex items-center relative pt-10">
      <div className="max-w-6xl mx-auto px-4 sm:px-8 lg:px-12 w-full">
        <div className="flex items-center justify-between gap-24 lg:gap-32 min-h-[60vh]">
          {/* Text Content - Left Side */}
          <div className="flex-1 space-y-8 relative z-10 text-left">
            <div className="space-y-6">
              <h1 className={`text-4xl md:text-5xl lg:text-6xl font-bold mb-6 ${theme === 'dark' ? 'text-white' : 'text-gray-900'}`}>
                Vihaan Gupta
              </h1>
              <p className={`text-sm md:text-base lg:text-lg text-white/50 max-w-2xl leading-relaxed font-sans ${theme === 'dark' ? 'text-white/70' : 'text-gray-700'}`}>
                Hi! I'm Vihaan, a rising Junior at Harvard College, studying Computer Science and Philosophy. 
                I'm passionate about human-centered design and creating social impact through technology.
                In my free time, I love reading philosophy, going to the gym, and exploring new restaurants. Happy to connect!
              </p>
            </div>

            <div className="flex space-x-6 pt-4">
              {[
                { icon: Github, href: "#" },
                { icon: Linkedin, href: "#" },
                { icon: Mail, href: "#" }
              ].map(({ icon: Icon, href }, i) => (
                <a
                  key={i}
                  href={href}
                  className="p-3 rounded-full bg-white/10 backdrop-blur-sm hover:bg-white/20 transition-all duration-300 hover:scale-110 group"
                >
                  <Icon size={24} className="group-hover:text-purple-400 transition-colors duration-300" />
                </a>
              ))}
            </div>
          </div>

          {/* Profile Picture - Right Side */}
          <div className="flex-shrink-0 relative">
            <div className="relative group">
              <div className="absolute"></div>

              <div className="relative w-56 h-56 md:w-[20rem] md:h-[20rem] lg:w-[24rem] lg:h-[24rem]">
                <img 
                  src="/images/Group 3.png"
                  alt="Profile Picture" 
                  className="w-full h-full object-cover"
                />

                <div className="absolute top-4 right-4 w-4 h-4 bg-purple-400 rounded-full animate-ping opacity-75"></div>
                <div className="absolute bottom-8 left-8 w-6 h-6 bg-pink-400 rounded-full animate-pulse opacity-60"></div>
                <div className="absolute top-1/3 left-4 w-3 h-3 bg-blue-400 rounded-full animate-bounce opacity-80"></div>
              </div>
            </div>
          </div>
        </div>

        {/* Scroll Indicator */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce">
          <ChevronDown size={32} className="text-purple-400" />
        </div>
      </div>
    </section>
  );
};

export default Hero;
