import React, { useState } from 'react';
import { Linkedin, Mail, X } from 'lucide-react';
import emailjs from '@emailjs/browser';

const Hero: React.FC = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [bookRecommendation, setBookRecommendation] = useState('');
  const [submitterName, setSubmitterName] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState<'idle' | 'success' | 'error'>('idle');

  const handleSubmitRecommendation = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitStatus('idle');

    try {
      emailjs.init('iUztHDq2ElQ2Kjzkr'); 

      const result = await emailjs.send(
        'service_czncobf', 
        'template_t2uxduc', 
        {
          book_recommendation: bookRecommendation,
          submitter_name: submitterName || 'Anonymous',
          to_email: 'vihaangupta@college.harvard.edu',
        }
      );

      console.log('Email sent successfully:', result);
      setSubmitStatus('success');
      setBookRecommendation('');
      setSubmitterName(''); // Reset the name field

      setTimeout(() => {
        setIsModalOpen(false);
        setSubmitStatus('idle');
      }, 2000);

    } catch (error) {
      console.error('Failed to send email:', error);
      setSubmitStatus('error');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <section id="home" className="min-h-[calc(100vh-5rem)] flex items-center relative">
        <div className="max-w-6xl mx-auto px-4 sm:px-8 lg:px-12 w-full">
          <div className="grid grid-cols-1 lg:grid-cols-[1.1fr_0.9fr] gap-12 items-center">
            <div className="space-y-10">
              <div className="flex items-center gap-4 text-[0.65rem] uppercase tracking-[0.6em] text-black/50">
                <span className="h-px w-10 bg-black/40" />
                Harvard · CS & Philosophy · 2026
              </div>

              <div className="space-y-6">
                <p className="text-sm uppercase tracking-[0.4em] text-black/50">
                  Building humane systems
                </p>
                <h1 className="text-4xl md:text-5xl lg:text-6xl xl:text-[4.2rem] font-semibold leading-[1.05]">
                  Designing calm, resilient technology for people who need their tools to simply work.
                </h1>
              </div>

              <p className="text-base md:text-lg text-black/70 max-w-2xl leading-relaxed">
                Hi, I&apos;m Vihaan—an engineering + design student obsessed with how infrastructure and
                interfaces shape behavior. I spend my days prototyping social impact tools, shipping production
                systems, and <button onClick={() => setIsModalOpen(true)} className="underline underline-offset-4 decoration-black/30 hover:decoration-black transition-colors duration-200">
                  trading philosophy books
                </button>{' '}with whoever will indulge me.
              </p>

              <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
                {[
                  { label: 'Current focus', value: 'Market infra + social products' },
                  { label: 'Recent roles', value: 'MIAX · As1 · Avalon' },
                  { label: 'What I optimize for', value: 'Clarity, trust, momentum' }
                ].map((item) => (
                  <div key={item.label} className="rounded-2xl border border-black/10 bg-white/70 backdrop-blur px-4 py-5">
                    <p className="text-xs uppercase tracking-[0.35em] text-black/40">{item.label}</p>
                    <p className="mt-2 text-sm font-medium text-black">{item.value}</p>
                  </div>
                ))}
              </div>

              <div className="flex flex-wrap gap-2">
                {['Human-centered systems', 'Ethical infra', 'Playful UX', 'Applied research'].map((tag) => (
                  <span key={tag} className="px-4 py-2 text-xs uppercase tracking-[0.35em] border border-black/10 rounded-full text-black/60">
                    {tag}
                  </span>
                ))}
              </div>

              <div className="flex flex-wrap gap-3">
                {[
                  { icon: Linkedin, href: 'https://www.linkedin.com/in/vihaan-gupta-1595332a7', text: 'LinkedIn' },
                  { icon: Mail, href: 'mailto:vihaangupta@college.harvard.edu', text: 'Email' }
                ].map(({ icon: Icon, href, text }) => (
                  <a
                    key={text}
                    href={href}
                    className="flex items-center gap-2 px-5 py-3 border border-black/15 rounded-full bg-white/70 backdrop-blur transition-all duration-300 hover:-translate-y-0.5 hover:border-black/40"
                  >
                    <Icon size={18} className="text-black/70" />
                    <span className="text-sm font-medium">{text}</span>
                  </a>
                ))}
              </div>
            </div>

            <div className="relative flex justify-center">
              <div className="relative w-full max-w-[22rem] aspect-[3/4] rounded-[2.5rem] border border-black/10 bg-white overflow-hidden shadow-[0_25px_80px_rgba(0,0,0,0.12)]">
                <img
                  src="/images/my-notion-face-transparent.png"
                  alt="Vihaan Gupta"
                  className="w-full h-full object-cover mix-blend-multiply"
                />
                <div className="absolute inset-6 border border-black/10 rounded-[2rem] pointer-events-none" />
              </div>

              <div className="absolute -left-8 top-8 bg-white border border-black/10 rounded-2xl px-5 py-4 shadow-sm">
                <p className="text-xs uppercase tracking-[0.4em] text-black/40">Years building</p>
                <p className="text-3xl font-semibold mt-2">6+</p>
                <p className="text-xs text-black/50 mt-1">product + infra stacks</p>
              </div>

              <div className="absolute -right-10 bottom-20 bg-white border border-black/10 rounded-2xl px-5 py-4 shadow-sm max-w-[220px]">
                <p className="text-xs uppercase tracking-[0.4em] text-black/40 mb-2">Have a rec?</p>
                <p className="text-sm text-black/70 mb-3">
                  I keep a running list of mind-bending philosophy reads.
                </p>
                <button
                  onClick={() => setIsModalOpen(true)}
                  className="text-sm font-medium underline underline-offset-4 decoration-black/30 hover:decoration-black"
                >
                  Recommend a book →
                </button>
              </div>

              <div className="absolute -bottom-10 left-1/2 -translate-x-1/2 bg-white border border-black/10 rounded-full px-6 py-3 shadow-sm text-sm text-black/70">
                Currently: architecting calm trading systems at MIAX.
              </div>
            </div>
          </div>
        </div>
      </section>

      {isModalOpen && (
        <div className="fixed inset-0 bg-white/90 flex items-center justify-center z-50 p-4">
          <div className="bg-white border border-black/20 p-8 max-w-md w-full relative">
            <button
              onClick={() => setIsModalOpen(false)}
              className="absolute top-4 right-4 text-black/60 hover:text-black transition-colors duration-300"
            >
              <X size={24} />
            </button>

            <h2 className="text-2xl font-bold mb-6 text-black">Recommend a Book</h2>
            <p className="text-black/60 mb-6">
              I&apos;d love to hear about a book that changed how you see the world.
            </p>

            <form onSubmit={handleSubmitRecommendation} className="space-y-6">
              <div>
                <label htmlFor="submitter-name" className="block text-sm font-medium text-black/80 mb-2">
                  Your Name:
                </label>
                <input
                  type="text"
                  id="submitter-name"
                  value={submitterName}
                  onChange={(e) => setSubmitterName(e.target.value)}
                  className="w-full p-3 border border-black/20 rounded-sm bg-white text-black placeholder-black/30 focus:outline-none focus:border-black/40 transition-colors duration-300"
                  placeholder="(optional)"
                  disabled={isSubmitting}
                />
              </div>

              <div>
                <label htmlFor="book-recommendation" className="block text-sm font-medium text-black/80 mb-2">
                  Book Title & Author:
                </label>
                <input
                  type="text"
                  id="book-recommendation"
                  value={bookRecommendation}
                  onChange={(e) => setBookRecommendation(e.target.value)}
                  className="w-full p-3 border border-black/20 rounded-sm bg-white text-black placeholder-black/30 focus:outline-none focus:border-black/40 transition-colors duration-300"
                  required
                  disabled={isSubmitting}
                />
              </div>

              {submitStatus === 'success' && (
                <div className="p-3 bg-black/10 border border-black/20 rounded-sm text-black text-sm">
                  Thank you! Your book recommendation has been sent successfully.
                </div>
              )}

              {submitStatus === 'error' && (
                <div className="p-3 bg-black/5 border border-black/20 rounded-sm text-black/60 text-sm">
                  Sorry, there was an error sending your recommendation. Please try again.
                </div>
              )}

              <div className="flex justify-end space-x-3">
                <button
                  type="button"
                  onClick={() => setIsModalOpen(false)}
                  className="px-6 py-2 border border-black/20 text-black/70 hover:border-black/40 hover:text-black hover:bg-black/5 transition-colors duration-300"
                  disabled={isSubmitting}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-6 py-2 bg-black text-white hover:bg-black/90 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed font-medium"
                  disabled={isSubmitting}
                >
                  {isSubmitting ? 'Sending...' : 'Submit'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default Hero;
