import React from 'react';

interface NavigationProps {
  activeTab: 'home' | 'experience' | 'projects' | 'contact';
  setActiveTab: (tab: 'home' | 'experience' | 'projects' | 'contact') => void;
}

const Navigation: React.FC<NavigationProps> = ({ activeTab, setActiveTab }) => {
  const tabs = [
    { id: 'home' as const, label: 'Home' },
    { id: 'experience' as const, label: 'Experience' },
    { id: 'projects' as const, label: 'Projects' },
    { id: 'contact' as const, label: 'Contact' }
  ];

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-white/90 backdrop-blur-lg border-b border-black/5">
      <div className="max-w-6xl mx-auto px-4 sm:px-8 lg:px-12">
        <div className="flex items-center justify-between h-20">
          <div className="flex items-center gap-4">
            <div className="w-11 h-11 rounded-full border border-black/20 flex items-center justify-center text-sm font-semibold tracking-[0.25em]">
              VG
            </div>
            <div className="leading-snug">
              <p className="uppercase text-[0.6rem] tracking-[0.6em] text-black/50">
                Portfolio
              </p>
              <p className="text-base sm:text-lg font-semibold text-black">Vihaan Gupta</p>
            </div>
          </div>

          <div className="flex items-center gap-1 px-2 py-2 border border-black/10 rounded-full bg-white/70 backdrop-blur">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`px-4 py-2 rounded-full text-sm font-medium tracking-wide transition-all duration-300 ${
                  activeTab === tab.id
                    ? 'bg-black text-white shadow-[0_12px_30px_rgba(0,0,0,0.15)]'
                    : 'text-black/60 hover:text-black hover:bg-black/5'
                }`}
              >
                {tab.label}
              </button>
            ))}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
