import { useEffect, useRef, useState } from 'react';

interface MousePosition {
  x: number;
  y: number;
}

export const useMouseGlow = () => {
  const [mousePosition, setMousePosition] = useState<MousePosition>({ x: 0, y: 0 });
  const currentPosition = useRef<MousePosition>({ x: 0, y: 0 });
  const frameRef = useRef<number>();

  const lerp = (start: number, end: number, t: number) => {
    return start * (1 - t) + end * t;
  };

  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      setMousePosition({
        x: e.clientX,
        y: e.clientY,
      });
    };

    const animate = () => {
      currentPosition.current = {
        x: lerp(currentPosition.current.x, mousePosition.x, 0.1),
        y: lerp(currentPosition.current.y, mousePosition.y, 0.1),
      };

      frameRef.current = requestAnimationFrame(animate);
    };

    window.addEventListener('mousemove', handleMouseMove);
    frameRef.current = requestAnimationFrame(animate);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      if (frameRef.current) {
        cancelAnimationFrame(frameRef.current);
      }
    };
  }, [mousePosition]);

  return currentPosition.current;
};
