import { useEffect, useRef } from 'react';

interface SmoothHoverOptions {
  targetValue: number;
  currentValue: number;
  speed?: number;
  onUpdate: (value: number) => void;
}

const lerp = (start: number, end: number, t: number) => {
  return start * (1 - t) + end * t;
};

export const useSmoothHover = ({ targetValue, currentValue, speed = 0.1, onUpdate }: SmoothHoverOptions) => {
  const frameRef = useRef<number>();
  const currentRef = useRef(currentValue);

  useEffect(() => {
    const animate = () => {
      currentRef.current = lerp(currentRef.current, targetValue, speed);
      onUpdate(currentRef.current);

      if (Math.abs(currentRef.current - targetValue) > 0.001) {
        frameRef.current = requestAnimationFrame(animate);
      }
    };

    frameRef.current = requestAnimationFrame(animate);

    return () => {
      if (frameRef.current) {
        cancelAnimationFrame(frameRef.current);
      }
    };
  }, [targetValue, speed, onUpdate]);

  return currentRef.current;
};
