import React from 'react';
import { useMouseGlow } from '../hooks/useMouseGlow';

export const MouseGlow: React.FC = () => {
  const position = useMouseGlow();

  return (
    <div
      className="pointer-events-none fixed inset-0 z-30 transition-transform duration-100"
      style={{
        transform: `translate(${position.x}px, ${position.y}px)`,
      }}
    >
      <div
        className="absolute -translate-x-1/2 -translate-y-1/2 w-[500px] h-[500px] rounded-full"
        style={{
          background: 'radial-gradient(circle, rgba(147, 51, 234, 0.15) 0%, rgba(147, 51, 234, 0.1) 25%, rgba(147, 51, 234, 0.05) 50%, transparent 70%)',
          filter: 'blur(40px)',
        }}
      />
    </div>
  );
};
