import React from 'react';
import { ChevronDown, Github, Linkedin, Mail } from 'lucide-react';

const Hero: React.FC = () => {
  return (
    <section id="home" className="min-h-screen flex items-center relative pt-20">
      <div className="max-w-6xl mx-auto px-8 sm:px-12 lg:px-16 w-full">
        <div className="flex items-center justify-between gap-12 lg:gap-16 min-h-[60vh]">
          {/* Text Content - Left Side */}
          <div className="flex-1 space-y-8 relative z-10 text-left">
            <div className="space-y-6">
              <h1 className="text-5xl md:text-6xl lg:text-7xl font-bold bg-gradient-to-r from-purple-400 via-pink-400 to-blue-400 bg-clip-text text-transparent animate-pulse leading-tight">
                John Doe
              </h1>
              <p className="text-lg md:text-xl lg:text-2xl text-gray-300 max-w-2xl leading-relaxed">
                Full-Stack Developer & UI/UX Designer crafting digital experiences with modern technologies
              </p>
            </div>

            <div className="flex space-x-6 pt-4">
              {[
                { icon: Github, href: "#" },
                { icon: Linkedin, href: "#" },
                { icon: Mail, href: "#" }
              ].map(({ icon: Icon, href }, i) => (
                <a
                  key={i}
                  href={href}
                  className="p-3 rounded-full bg-white/10 backdrop-blur-sm hover:bg-white/20 transition-all duration-300 hover:scale-110 group"
                >
                  <Icon size={24} className="group-hover:text-purple-400 transition-colors duration-300" />
                </a>
              ))}
            </div>
          </div>

          {/* Profile Picture - Right Side */}
          <div className="flex-shrink-0 relative">
            <div className="relative group">
              <div className="absolute -inset-1 bg-gradient-to-r from-purple-400 via-pink-400 to-blue-400 rounded-full blur opacity-75 group-hover:opacity-100 transition duration-1000 group-hover:duration-200 animate-pulse"></div>

              <div className="relative w-80 h-80 md:w-[28rem] md:h-[28rem] lg:w-[32rem] lg:h-[32rem]">
                <img 
                  src="/images/Group 3.png"
                  alt="Profile Picture" 
                  className="w-full h-full object-cover"
                />

                <div className="absolute top-4 right-4 w-4 h-4 bg-purple-400 rounded-full animate-ping opacity-75"></div>
                <div className="absolute bottom-8 left-8 w-6 h-6 bg-pink-400 rounded-full animate-pulse opacity-60"></div>
                <div className="absolute top-1/3 left-4 w-3 h-3 bg-blue-400 rounded-full animate-bounce opacity-80"></div>
              </div>
            </div>
          </div>
        </div>

        {/* Scroll Indicator */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce">
          <ChevronDown size={32} className="text-purple-400" />
        </div>
      </div>
    </section>
  );
};

export default Hero;
