import React, { useState, useCallback } from 'react';
import { useSmoothHover } from '../hooks/useSmoothHover';

interface SmoothHoverCardProps {
  children: React.ReactNode;
  className?: string;
}

export const SmoothHoverCard: React.FC<SmoothHoverCardProps> = ({ children, className = '' }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [opacity, setOpacity] = useState(0);

  const handleOpacityUpdate = useCallback((value: number) => {
    setOpacity(value);
  }, []);

  useSmoothHover({
    targetValue: isHovered ? 1 : 0,
    currentValue: opacity,
    speed: 0.1,
    onUpdate: handleOpacityUpdate,
  });

  return (
    <div
      className={`group rounded-xl p-6 transition-all duration-300 ${className}`}
      style={{
        backgroundColor: `rgba(255, 255, 255, ${opacity * 0.05})`,
        backdropFilter: `blur(${opacity * 8}px)`,
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {children}
    </div>
  );
};
